&quot;); &#125; printf(&quot;
&quot;);*/ for(int i=1;i&lt;=n;++i) &#123; getchar(); for(int j=1;j&lt;=m;++j) &#123; scanf(&quot;%c&quot;,&amp;temp); switch (temp) &#123; case &#x27;Y&#x27;: &#123; YFF.X = i; YFF.Y = j; Y[i][j] = 0; M[i][j] = 0; break; &#125; case &#x27;M&#x27;:&#123; MCK.X = i; MCK.Y = j; Y[i][j] = 0; M[i][j] = 0; break; &#125; case &#x27;#&#x27;:&#123; Y[i][j] = -1; M[i][j] = -1; break; &#125; case &#x27;.&#x27;:&#123; Y[i][j] = 0; M[i][j] = 0; break; &#125; case &#x27;@&#x27;:&#123; Y[i][j] = 0; M[i][j] = 0; KFC.push(&#123;i,j&#125;); break; &#125; &#125; &#125; &#125; que.push(&#123;YFF,1&#125;); Y[YFF.X][YFF.Y] = 1; go(Y); Y[YFF.X][YFF.Y] = 0; que.push(&#123;MCK,1&#125;); M[MCK.X][MCK.Y] = 1; go(M); M[MCK.X][MCK.Y] = 0; unsigned long long min = -1; while(!KFC.empty()) &#123; Pos temp = KFC.front(); KFC.pop(); if(Y[temp.X][temp.Y]==0||M[temp.X][temp.Y]==0) continue; // 道路被封死 if(min&gt;Y[temp.X][temp.Y]+M[temp.X][temp.Y]) &#123; min = Y[temp.X][temp.Y]+M[temp.X][temp.Y]; &#125; &#125; printf(&quot;%lld
